import React, { useState } from 'react';  
import { BrowserRouter as Router, Route, Routes, useLocation, Link } from 'react-router-dom';  
import Menu from './components/menupage';  
import UserOrders from './components/UserOrders';  
import AdminOrders from './components/AdminOrders';  
import Home from './components/Home';  
import CustomizePizza from './components/CustomizePizza';  
import Layout from './components/Layout';  
import './components/App.css'; // Import custom CSS  
import AdminPage from './components/AdminPage';  
import Login from './components/Login'; // Import the Login component  
import ProfilePage from './components/ProfilePage'; // Import the ProfilePage component  
import { GoogleOAuthProvider } from '@react-oauth/google';  

function App() {  
  const [accessToken, setAccessToken] = useState('');  
  const [error, setError] = useState('');  

  return (  
    <GoogleOAuthProvider clientId="YOUR_GOOGLE_CLIENT_ID">  
      <Router>  
        <ConditionalLayout>  
          <Routes>  
            <Route path="/" element={<Home />} />  
            <Route path="/user-orders" element={<UserOrders accessToken={accessToken} />} />  
            <Route path="/admin-orders" element={<AdminOrders />} />  
            <Route path="/menu" element={<Menu />} />  
            <Route path="/customize" element={<CustomizePizza />} />  
            <Route path="/admin" element={<AdminPage />} />  
            <Route path="/login" element={<Login setAccessToken={setAccessToken} setError={setError} />} />  
            <Route path="/profile" element={<ProfilePage />} /> {/* New route for Profile */}  
            {/* Add more routes as needed */}  
          </Routes>  
        </ConditionalLayout>  
      </Router>  
    </GoogleOAuthProvider>  
  );  
}  

const ConditionalLayout = ({ children }) => {  
  const location = useLocation();  

  // Define the routes you want to exclude from the layout  
  const pathsWithoutLayout = ['/login', '/admin'];  

  // Check if the current path is one of the excluded paths  
  const shouldRenderWithoutLayout = pathsWithoutLayout.includes(location.pathname);  

  return (  
    shouldRenderWithoutLayout ?   
      <>{children}</> :   
      <Layout>  
        <Navigation /> {/* Include the Navigation component */}  
        {children}  
      </Layout>  
  );  
};  

// Navigation component  
const Navigation = () => {  
  // return (  
  //   <nav style={{ padding: '10px', background: '#FF7F32', color: 'white' }}>  
  //     <Link to="/" style={navButtonStyle}>Home</Link>  
  //     <Link to="/menu" style={navButtonStyle}>Menu</Link>  
  //     <Link to="/admin" style={navButtonStyle}>Admin Panel</Link>  
  //   </nav>  
  // );  
};  

const navButtonStyle = {  
  margin: '0 10px',  
  padding: '10px 20px',  
  color: '#fff',  
  background: '#FF5C00',  
  border: 'none',  
  borderRadius: '5px',  
  cursor: 'pointer'  
};  

export default App;
