import React from "react";  
import './ProfilePage.css';

const ProfilePage = () => {  
  const customerData = {  
    name: "John Doe",  
    address: "123 Pizza Lane, Flavor Town, FL 12345",  
    orderHistory: [  
      { id: 1, item: "Pepperoni Pizza", date: "2024-01-15", status: "Delivered" },  
      { id: 2, item: "Margherita Pizza", date: "2024-01-20", status: "Delivered" },  
      { id: 3, item: "BBQ Chicken Pizza", date: "2024-01-25", status: "Pending" },  
    ],  
  };  

  return (  
    <div className="container">  
      <h1>Profile Information</h1>  
      <div className="form-container">  
        <h2>Personal Details</h2>  
        <label>Name:</label>  
        <input type="text" value={customerData.name} readOnly />  

        <label>Address:</label>  
        <textarea value={customerData.address} readOnly />  

        <h2>Order History</h2>  
        <ul>  
          {customerData.orderHistory.map(order => (  
            <li key={order.id}>  
              {order.item} - {order.date} - <strong>{order.status}</strong>  
            </li>  
          ))}  
        </ul>  
      </div>  
    </div>  
  );  
};  

export default ProfilePage;
